mapper.select:
SELECT SITE_CODE, SITE_NAME, LATITUDE, LONGITUDE, ADDRESS, CAM_SERIAL_NUM, AI_PORT, CRT_DTIME, GRID_X, GRID_Y FROM T_SITE_INFO
AND SITE_CODE LIKE CONCAT('%', #{siteCode}, '%')
ORDER BY CRT_DTIME DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT IFNULL(MAX(LAST_SERIAL_NUM), 0)
FROM T_SITE_CODE
WHERE REGION_CODE = #{regionCode}
AND CITY_CODE = #{cityCode}

mapper.update:
UPDATE T_SITE_CODE
SET LAST_SERIAL_NUM = #{lastSerialNum}
WHERE REGION_CODE = #{regionCode}
AND CITY_CODE = #{cityCode}
